class AccentState:
    ACCENT_DISABLED = 0
    ACCENT_ENABLE_GRADIENT = 1
    ACCENT_ENABLE_TRANSPARENTGRADIENT = 2
    ACCENT_ENABLE_BLURBEHIND = 3
    ACCENT_ENABLE_ACRYLICBLURBEHIND = 4
    ACCENT_ENABLE_HOSTBACKDROP = 5

class AccentFlags:
    NONE = 0
    DRAW_LEFT_BORDER = 0x20
    DRAW_TOP_BORDER = 0x40
    DRAW_RIGHT_BORDER = 0x80
    DRAW_BOTTOM_BORDER = 0x100
    DRAW_ALL_BORDERS = DRAW_LEFT_BORDER | DRAW_TOP_BORDER | DRAW_RIGHT_BORDER | DRAW_BOTTOM_BORDER

class AnimationId:
    NONE = 0
    FADE = 1
    SLIDE = 2
    BLEND = 3

class GradientColors:
    TRANSPARENT_BLACK = 0x00000000
    SEMI_BLACK = 0x80000000
    DARK_GRAY = 0xFF1E1E1E
    BLUE_TINT = 0xFFFF0000
    PURPLE_TINT = 0xFFFF0080
    ORANGE_TINT = 0xFF0080FF
    GREEN_TINT = 0xFF00FF00
    RED_TINT = 0xFF0000FF
    WHITE_TINT = 0xFFFFFFFF
    LIGHT_BLUE = 0xFFFFBF00
    DARK_BLUE = 0xFF3F1F00
    CYAN_TINT = 0xFFFFFF00
    YELLOW_TINT = 0xFF00FFFF
    PINK_TINT = 0xFFB469FF
