    [UnmanagedCallConv(CallConvs = [typeof(CallConvCdecl)])]
    internal static partial lex_t* lex_new(nuint symbols, nuint keywords, string? simpleComment, string? commentStart, string? commentStop, string? javadocCommentStart, string? stringDelim, int flags);

    internal static unsafe lex_t* lex_newString(out string? symbols, out string? keywords, string? simpleComment, string? commentStart, string? commentStop, string? javadocCommentStart, string? stringDelim, int flags)
    {
        nuint symbolsPtr = 0;
        nuint keywordsPtr = 0;
        lex_t* result = lex_new((nuint)(&symbolsPtr), (nuint)(&keywordsPtr), simpleComment, commentStart, commentStop, javadocCommentStart, stringDelim, flags);
        if (symbolsPtr == 0)
        {
            symbols = null;
        }
        else
        {
            symbols = Marshal.PtrToStringUTF8((nint)symbolsPtr);
        }
        if (keywordsPtr == 0)
        {
            keywords = null;
        }
        else
        {
            keywords = Marshal.PtrToStringUTF8((nint)keywordsPtr);
        }
        return result;
    }

