#!/usr/bin/env python
#
# ----------------------------------------------------------------------------

from __future__ import annotations

import copy

from typing import Any, Dict


# ============================================================================

class DeepClone:

    # ------------------------------------------------------------------------

    def __init__(self) -> None:
        self._objects: Dict[int, Any] = {}

    # ------------------------------------------------------------------------

    def add(self, _id: int, obj: Any) -> None:
        self._objects[int(_id)] = copy.deepcopy(obj)

    # ------------------------------------------------------------------------

    def get(self, _id: int) -> Any:
        return self._objects[int(_id)]

    # ------------------------------------------------------------------------


# ============================================================================
