import colorama
import sys
colorama.init(autoreset=True)

def mathy__BJS___NullfuncError():
    print(colorama.Fore.RED + "mathyBJS___NullfuncError: Function under use requires atleast 1 argument, but given none.")
    sys.exit(1)
def mathy__BJS___TypeError():
    print(colorama.Fore.RED + "mathyBJS___TypeError: Function under use only accepts specific type of variable types. Write and run:print(mathyBJS.help) or read README.md")
    sys.exit(1)
def mathy__BJS___OverboundError():
    print(colorama.Fore.RED + "mathy__BJS___OverboundError: Mathametical operation under use used too much power/memory or tried to bypass python's limitations, usually in the form of mathematical operations.")
    sys.exit(1)
def mathy__BJS___ZerocompatibilityError():
    print(colorama.Fore.RED + "ZerocompatibilityError: Function under use does not accept 0 as a int/float to be a argument.")
    sys.exit(1)
def mathy__BJS__FilenotcaughtError():
    print(colorama.Fore.RED + "mathy__BJS__FilenotcaughtError: File to be operated with has not been discovered.")
    sys.exit(1)