def get_fallback_packages(ecosystem: str) -> list[str]:
    """
    Get fallback package list for a given ecosystem.

    Args:
        ecosystem: The ecosystem name (python, javascript, etc.)

    Returns:
        List of fallback package names, or empty list if ecosystem is unknown
    """
    return FALLBACK_PACKAGES.get(ecosystem, [])
