import os

from checks.base import Checker
from problem import Problem, Severity

class CommentsChecker(Checker):
    MAGIC = 0xC0E3

    SUSPICIOUS = [
        "do not modify this file",
        "do not edit this file",
        "do not edit by hand",
        "do not modify by hand",
        "do not edit!",
        "do not modify!",
        "do not edit.",
        "do not modify.",
        "do not edit manually",
        "do not modify manually",
        "file was generated",
        "this was generated",
        "it was generated",
        "file is generated",
        "this is generated",
        "it is generated",
        "file is automatically generated",
        "this is automatically generated",
        "it is automatically generated",
        "automatically generated by",
        "automatically generated from",
        "automatically generated for",
        "file is generated automatically",
        "this is generated automatically",
        "it is generated automatically ",
        "generated automatically by",
        "generated automatically from",
        "generated automatically for",
        "machine generated",
        "file generated by",
        "this file generated",
        "file generated from",
        "file generated for",
        "was generated by",
        "has been generated by",
        "will be generated by",
        "file is autogenerated",
        "file autogenerated",
        "autogenerated file",
        "is generated code",
        "is generated using",
        "was generated using",
        "be generated using",
        "generated code follow",
        "this generated code",
        "is derived from",
        "file is derived",
        "file derived from",
    ]

    EXACTS = [
        "# Generated by ",
        "# generated by ",
        "GENERATED BY ",
        "* Generated by ",
        "* generated by ",
        "* GENERATED BY ",
        "# Generated from ",
        "# generated from ",
        "# GENERATED FROM ",
        "* Generated from ",
        "* generated from ",
        "* GENERATED FROM ",
        "GENERATED CODE",
    ]

    STARTSWITH_FILENAME_WHITELIST = [
        "Changelog",
        "ChangeLog",
    ]

    # Checks for common strings that indicate generated files
    def execute(self, file: str) -> Problem | None:
        for name in self.STARTSWITH_FILENAME_WHITELIST:
            if file.startswith(name):
                return None

        with open(file, "rb") as f:
            text = f.read()
            for string in self.SUSPICIOUS:
                for variant in [string, string.capitalize(), string.upper()]:
                    if variant.encode("utf-8") in text:
                        return Problem(Severity.WARN, f"\"{variant}\" may indicate this file is generated, or is a generator", file, self.MAGIC)
            for string in self.EXACTS:
                if string.encode("utf-8") in text:
                    return Problem(Severity.WARN, f"\"{string}\" may indicate this file is generated, or is a generator", file, self.MAGIC)
        return None
