    def chunk_string(self, text, length):
        try:
            words = iter(text.split())
            lines, current = [], next(words)
            for word in words:
                if len(current) + 1 + len(word) > length:
                    lines.append(current)
                    current = word
                else:
                    current += " " + word
            lines.append(current)
            return lines
        except Exception as e:
            tb = sys.exc_info()[2]
            LOG.error('<%s> in line %s !!!' % (str(e),tb.tb_lineno))
            
