# ============================================================================
# HELPER FUNCTIONS
# ============================================================================

def generate_wsgi(project_dir: str) -> None:
    """Generate wsgi.py in the project directory"""
    wsgi_path = os.path.join(project_dir, 'wsgi.py')
    with open(wsgi_path, 'w') as f:
        f.write(WSGI_TEMPLATE)


def generate_procfile(project_dir: str) -> None:
    """Generate Procfile in the project directory"""
    procfile_path = os.path.join(project_dir, 'Procfile')
    with open(procfile_path, 'w') as f:
        f.write(PROCFILE_TEMPLATE)


def generate_requirements(project_dir: str) -> None:
    """Generate requirements.txt in the project directory"""
    req_path = os.path.join(project_dir, 'requirements.txt')
    with open(req_path, 'w') as f:
        f.write(REQUIREMENTS_TEMPLATE)


def generate_production_files(project_dir: str) -> None:
    """
    Generate all production deployment files.
    Called automatically during `volta build` or `volta deploy`.
    """
    generate_wsgi(project_dir)
    generate_procfile(project_dir)
    generate_requirements(project_dir)
