import sys
import pytest
from pathlib import Path
from datetime import datetime
import io

# Set UTF-8 encoding for Windows console
if sys.platform == 'win32':
    try:
        sys.stdout = io.TextIOWrapper(sys.stdout.buffer, encoding='utf-8')
        sys.stderr = io.TextIOWrapper(sys.stderr.buffer, encoding='utf-8')
    except:
        pass


def main():
    """Run tests for data module."""
    # Add src to Python path
    src_path = Path(__file__).parent.parent.parent.parent / "src"
    sys.path.insert(0, str(src_path))
    
    module_name = Path(__file__).parent.name.replace('_tests', '')
    print(f"   Testing: {module_name}")
    
    # Run module tests
    exit_code = pytest.main([
        "-v",
        "--tb=short",
        str(Path(__file__).parent),
        "-m", "xwdata_unit"
    ])
    
    # Save Markdown output
    status = "PASSED" if exit_code == 0 else "FAILED"
    output_file = Path(__file__).parent / "runner_out.md"
    
    markdown_content = f"""# Module Test Results: {module_name}

**Module:** {module_name}
**Layer:** 1.unit
**Generated:** {datetime.now().strftime("%d-%b-%Y %H:%M:%S")}
**Status:** {status}

---

## Result

{'✅ PASSED' if exit_code == 0 else '❌ FAILED'}

**Exit Code:** {exit_code}

---

*Generated by eXonware test runner - Production Excellence Edition*
"""
    
    output_file.write_text(markdown_content, encoding='utf-8')
    
    sys.exit(exit_code)


if __name__ == "__main__":
    main()

